    public static ArrayList<CClubs> getListData() {
        CClubs cClubs;
        ArrayList<CClubs> list = new ArrayList<>();

        for (String[] mData : data) {
            cClubs = new CClubs();
            cClubs.setName(mData[0]);
            cClubs.setOverview(mData[1]);
            cClubs.setPhoto(mData[2]);
            cClubs.setDescription(mData[3]);


            list.add(cClubs);
        }

        return list;
    }


